import React, { Component } from "react";
import { useState } from 'react';
import Form from "react-bootstrap/Form";
import DatePicker from "react-datepicker";
import axios from "axios";
import {Container, Row, Col, Button} from "react-bootstrap";

import "react-datepicker/dist/react-datepicker.css";

class Agenda extends Component {

  constructor(props) {
    super(props);
    this.state = {
        startDate: new Date(),
        title: "",
        location: [0,0]
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.longtitudes = [4.895168, 4.4777325, 5.1214201, 2.3522219];
    this.latitudes = [52.370216, 51.9244201, 52.0907374, 48.856614];
  }

  handleChange(evt) {
      if (evt.target.name == "location"){
          if (evt.target.value == "Amsterdam"){
              this.setState({ [evt.target.name]: [this.longtitudes[0], this.latitudes[0]] });
          }
          if (evt.target.value == "Rotterdam"){
              this.setState({ [evt.target.name]: [this.longtitudes[1], this.latitudes[1]] });
          }
          if (evt.target.value == "Utrecht"){
              this.setState({ [evt.target.name]: [this.longtitudes[2], this.latitudes[2]] });
          }
          if (evt.target.value == "Parijs"){
              this.setState({ [evt.target.name]: [this.longtitudes[3], this.latitudes[3]] });
          }
          else{
              return false;
          }
      } else{
          this.setState({ [evt.target.name]: evt.target.value });
      }
  }

  handleSubmit = event => {
    event.preventDefault();
    const date = this.state.startDate
    const title = this.state.title
    const location = this.state.location
    axios.post(`https://jsonplaceholder.typicode.com/users`, { date: date, title: title , location: location})
    .then(res => {
      console.log(res);
      console.log(res);
    }).catch(function (error) {
        console.log(error);
    });
  }

  render() {
    return (
      <div className="agenda">
        <Container>
          <h3>Add agenda item</h3>
            <Form>
              <Form.Group as={Row} controlId="Title">
                <Col sm={10}>
                  <Form.Control value={this.state.title} name="title" onChange={this.handleChange()}type="title" placeholder="Title" />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="datepicker" className=" datepicker-wrapper">
                <DatePicker
                  selected={this.state.startDate}
                  onChange={this.handleChange}
                  timeFormat="HH:mm"
                  timeIntervals={15}
                  dateFormat="MMMM d, yyyy h:mm aa"
                  timeCaption="time"
                  isClearable={true}
                  name="datepicker"
                  className="datepicker form-control"
                />
              </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Control as="select" placeholder="Location" name="location">
                        <option>Amsterdam</option>
                        <option>Prarijs</option>
                        <option>Rotterdam</option>
                        <option>Utrecht</option>
                    </Form.Control>
                </Form.Group>
              <Form.Group as={Row}>
                <Col sm={{ span: 10, offset: 2 }}>
                  <Button onClick={this.handleSubmit}>Add</Button>
                </Col>
              </Form.Group>
            </Form>
        </Container>
      </div>
    );
  }
}

export default Agenda;
