import React, { Component } from "react";
import { useState } from 'react';
import Form from "react-bootstrap/Form";
import DatePicker from "react-datepicker";
import axios from "axios";
import {Container, Row, Col, Button} from "react-bootstrap";

import "react-datepicker/dist/react-datepicker.css";

class Agenda extends Component {

  constructor(props) {
    super(props);
    this.state = {
        startDate: new Date(),
        title: "",
        location: [0,0],
        data: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  fetchData= async() =>{
    const response = await fetch('http://localhost:1348/user')
    const agenda = await response.json();
    this.setState({data:agenda})
}
    componentDidMount(){
      this.fetchData()
    }
  handleChange(evt) {
      console.log(evt.target.value)
      this.setState({ [evt.target.name]: evt.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();
    const date = this.state.startDate
    const title = this.state.title
    const location = this.state.location
    axios.post(`https://jsonplaceholder.typicode.com/users`, { date: date, title: title, location: location})
    .then(res => {
      console.log(res);
    }).catch(function (error) {
        console.log(error);
    });
  }

  render() {
    return (
      <div className="agenda">
        <Container>
          <h3>Add agenda item</h3>
            <Form>
              <Form.Group as={Row} controlId="Title">
                <Col sm={10}>
                  <Form.Control value={this.state.title} name="title" onChange={this.handleChange} type="title" placeholder="Title" />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="datepicker" className=" datepicker-wrapper">
                <DatePicker
                  selected={this.state.startDate}
                  onChange={this.handleChange}
                  timeFormat="HH:mm"
                  timeIntervals={15}
                  dateFormat="MMMM d, yyyy h:mm aa"
                  timeCaption="time"
                  isClearable={false}
                  name="datepicker"
                  className="datepicker form-control"
                />
              </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Control as="select" placeholder="Location" name="location" value={this.state.location} onChange={this.handleChange}>
                        <option value={[4.895168, 52.370216]}>Amsterdam</option>
                        <option value={[4.4777325, 51.9244201]}>Prarijs</option>
                        <option value={[5.1214201, 52.0907374]}>Rotterdam</option>
                        <option value={[2.3522219, 48.856614]}>Utrecht</option>
                    </Form.Control>
                </Form.Group>
              <Form.Group as={Row}>
                <Col sm={{ span: 10, offset: 2 }}>
                  <Button onClick={this.handleSubmit}>Add</Button>
                </Col>
              </Form.Group>
            </Form>
        </Container>
      </div>
    );
  }
}

export default Agenda;
