import React, { Component } from "react";
import { useState } from 'react';
import Form from "react-bootstrap/Form";
import DatePicker from "react-datepicker";
import axios from "axios";
import {Container, Row, Col, Button} from "react-bootstrap";

import "react-datepicker/dist/react-datepicker.css";
import {init, runAi} from '../training'
class Agenda extends Component {

  constructor() {
    super();
    init()
    this.state = {
        startDate: new Date(),
        title: "",
        location: [0,0],
        data: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

    fetchData= async() =>{
        const response = await fetch('http://localhost:1348/user')
        const agenda = await response.json();
        this.setState({data:agenda})
    }
    componentDidMount(){
      this.fetchData();
    }

    handleChange(evt) {
      this.setState({ [evt.target.name]: evt.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();
        let date = this.state.startDate
        let title = this.state.title
        let location = this.state.location
        let aiData = []
        aiData = this.makeFloats(title, date, location)
        axios.post(`https://jsonplaceholder.typicode.com/users`, { date: date, title: title, location: location})
        .then(res => {
            runAi(title_float, date_float, long_float, lat_float)
          runAi(aiData.title, aiData.date, aiData.long, aiData.lat)
        }).catch(function (error) {
            console.log(error);
        });
    }
    makeFloats(title, date, location){
        title = 0 + Math.random() * (19 - 0)
        let name_index
        let aiData = [];

        name_index = parseInt(title, 10)
        aiData.push(name_index/19);

        if(date != "0000-00-00 00:00:00"){
            aiData.push(1/(Date.parse(raw[row].date)/1000000000000));
        }else{
            aiData.push(0.7)
        }

        aiData.push( 1/location[0].longtitude)
        aiData.push(10/location[1].latitude)


        return aiData
    }
    render() {
    return (
      <div className="agenda">
        <Container>
          <h3>Add agenda item</h3>
            <Form>
              <Form.Group as={Row} controlId="Title">
                <Col sm={10}>
                  <Form.Control value={this.state.title} name="title" onChange={this.handleChange} type="title" placeholder="Title" />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="datepicker" className=" datepicker-wrapper">
                <DatePicker
                  selected={this.state.startDate}
                  onChange={this.handleChange}
                  timeFormat="HH:mm"
                  timeIntervals={15}
                  dateFormat="MMMM d, yyyy h:mm aa"
                  timeCaption="time"
                  isClearable={false}
                  name="datepicker"
                  className="datepicker form-control"
                />
              </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Control as="select" placeholder="Location" name="location" value={this.state.location} onChange={this.handleChange}>
                        <option value={[4.895168, 52.370216]}>Amsterdam</option>
                        <option value={[4.4777325, 51.9244201]}>Parijs</option>
                        <option value={[5.1214201, 52.0907374]}>Rotterdam</option>
                        <option value={[2.3522219, 48.856614]}>Utrecht</option>
                    </Form.Control>
                </Form.Group>
              <Form.Group as={Row}>
                <Col sm={{ span: 10, offset: 2 }}>
                  <Button onClick={this.handleSubmit}>Add</Button>
                </Col>
              </Form.Group>
            </Form>
        </Container>
      </div>
    );
    }
    }

    export default Agenda;
